import oggmfrom oggm import utils, workflow, cfgfrom oggm.utils import floatyear_to_date, hydrodate_to_calendardatefrom oggm.sandbox import distribute_2dfrom oggm.sandbox.edu import run_constant_climate_with_biasfrom oggm.core import gisimport geopandas as gpdimport matplotlib.pyplot as pltimport matplotlib.cm as cmimport xarray as xrimport osimport seaborn as snsimport salemfrom matplotlib.ticker import FuncFormatterfrom shapely.geometry import Point import pandas as pdimport numpy as npfrom matplotlib import animationimport cartopy.crs as ccrsimport cartopy.feature as cfeaturefrom IPython.display import HTML, displayfrom matplotlib.colors import ListedColormapfrom matplotlib.colors import LinearSegmentedColormap# cmap=cm.get_cmap('bone')# colors=[cmap(i / 5) for i in range(6)]fig, ax = plt.subplots(figsize=(20, 10), subplot_kw={'projection': ccrs.PlateCarree()})colors=['red', 'cyan', 'blue', 'purple', 'black']models=["W5E5", "IPSL-CM6","E3SM","CESM2", "CNRM"] mode_suff='total'for (i,model) in enumerate(models):     color_code=colors[i]        """ Load the data """    if model=="W5E5":        w5e5_path = '/Users/magaliponds/Library/CloudStorage/OneDrive-VrijeUniversiteitBrussel/1. VUB/02. Coding/01. IRRMIP/02. Data/01. Input files/W5E5/huss_W5E5v2.0_20110101-20191231.nc'        # print(w5e5_path)        grid=xr.open_dataset(w5e5_path)        linestyle="dashed"    else:        folder=f"/Users/magaliponds/OneDrive - Vrije Universiteit Brussel/1. VUB/02. Coding/01. IRRMIP/02. Data/01. Input files/{model}/"           ifile_IRR=f"{model}.IRR.000.1985_2014_selparam_monthly_{mode_suff}.nc"        linestyle="dashed"            #first for each variable load the data, for precipitation this consists of Snow & Rain from atmosphere, converted from [mm/day]        grid =xr.open_dataset(folder+ifile_IRR)            #cut out the  relevants seciton to later plot on the grid    grid = grid.where((grid.lon >= 88) & (grid.lon <= 97) & (grid.lat >= 28) & (grid.lat <= 37), drop=True)    #define the lon and latitude poitns in the dataset    lon = grid.lon    lat = grid.lat        print(model, lon[1:5].values, lat[1:5].values)    #create a meshgrid out of the lon and latitude points    lon_grid, lat_grid = np.meshgrid(lon, lat)    # print(lon_grid, lat_grid)        # Plot the grid lines, using a scatter graph for the corners and connect htem in a loop    for j in range(len(lon)):        for k in range(len(lat)):            if j < len(lon) - 1 and k < len(lat) - 1:                ax.plot([lon[j], lon[j+1]], [lat[k], lat[k]], linestyle=linestyle, color=color_code, alpha=0.6, dashes=[5,5])                ax.plot([lon[j], lon[j]], [lat[k], lat[k+1]], linestyle=linestyle, color=color_code, alpha=0.6, dashes=[5,5])                # ax.fill_between([lon[j], lon[j+1]], lat[k], lat[k+1], color=color_code, alpha=0.3)  # Fill squares    ax.scatter(lon_grid, lat_grid, color=color_code, marker='s', label=model, s=50)#modify the figureax.set_xlim((90,95))  # Adjust as neededax.set_ylim((30,35))  # Adjust as neededax.set_xticks(np.arange(90, 96, 1))  # Adjust as neededax.set_yticks(np.arange(30, 36, 1))  # Adjust as neededax.set_ylabel('Latitude °')ax.set_xlabel('Longitude °')ax.legend()ax.set_title('Data Product Grid by Climate model')plt.tight_layout()# plt.subplots_adjust(left=0.2, bottom=0.1, right=0.9, top=0.9)plt.show()        # Add geographic features    # ax.add_feature(cfeature.COASTLINE)    # ax.add_feature(cfeature.BORDERS)    # ax.add_feature(cfeature.LAND, edgecolor='black')            